import React from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
const ListaPersonajes = ({ personajes }) => {
  const seleccionarPersonaje = (personaje) => {
    console.log("asdasdas:", personaje);
  };

  return (
    <div>
      {personajes.map((personaje) => (
        <div onClick={seleccionarPersonaje(personaje)} key={personaje.id}>
          <Link to={`/personajes/${personaje.id}`}>
            <img src={personaje.image} />
          </Link>
        </div>
      ))}
    </div>
  );
};

const mapStateToProps = (state) => ({
  personajes: state.data.personajes,
});

export default connect(mapStateToProps)(ListaPersonajes);
