import { combineReducers } from "redux";

const initialState = {
  personajes: [],
  favoritos: [],
  categorias: [],
};

const rootReducer = combineReducers({
  data: (state = initialState, action) => {
    switch (action.type) {
      case "CARGAR_PERSONAJES":
        return {
          ...state,
          personajes: action.paylaod,
        };

      case "CARGAR_CATEGORIAS":
        return {
          ...state,
          categorias: action.paylaod,
        };
      default:
        return state;
    }
  },
});
